SELECT a1.machine_id, ROUND(AVG(a2.timestamp - a1.timestamp),3) AS processing_time FROM Activity AS a1 JOIN Activity AS a2 ON a2.machine_id = a1.machine_id and a1.process_id = a2.process_id and a2.activity_type = 'end' and a1.activity_type = 'start' GROUP BY machine_id;